function fxRandRanged(n,t){var r=Math.max(n,t),e=Math.min(n,t);let a=r-e;return fxrand()*a+e}function symmetricalRand(n){return fxRandRanged(-n,n)}function randomInt(n,t){let r=t-n+1;return Math.floor(.9999*fxrand()*r+n)}function randomFromRange(n){return n[randomInt(0,n.length-1)]}function normAngle(n){return n/180*Math.PI}function rgb(n,t,r){return[n/255,t/255,r/255]}function hexColor(n){var t=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(n);return t?rgb(parseInt(t[1],16),parseInt(t[2],16),parseInt(t[3],16)):null}function createBuffer(n,t){let r=createGraphics(xBufferSize,yBufferSize,WEBGL);return r.pixelDensity(1),r.width=n,r.height=t,r}function getPhi(n,t){return n>0&t>=0?Math.atan(t/n):n>0&t<0?Math.atan(t/n)+2*Math.PI:n<0?Math.atan(t/n)+Math.PI:Math.PI/2}function getDecart(n,t){return[n*Math.cos(t),n*Math.sin(t)]}function outOfScene(n,t){return n>xBufferSize||n<0||t>yBufferSize||t<0}class RandParam{constructor(n){this.values=n}get(){return randomFromRange(this.values)}}function degreeToRadian(n){return n*Math.PI/180}