function fxRandRanged(t,n){var r=Math.max(t,n),a=Math.min(t,n);let e=r-a;return fxrand()*e+a}function randomInt(t,n){let r=n-t+1;return Math.floor(.9999*fxrand()*r+t)}function randomFromRange(t){return t[randomInt(0,t.length-1)]}function normAngle(t){return t/180*Math.PI}function getPhi(t,n){return t>0&n>=0?Math.atan(n/t):t>0&n<0?Math.atan(n/t)+2*Math.PI:t<0?Math.atan(n/t)+Math.PI:Math.PI/2}function getDecart(t,n){return[t*Math.cos(n)+xBufferSize/2,t*Math.sin(n)+yBufferSize/2]}function getDecartShifted(t,n,r,a){return[t*Math.cos(n)+r,t*Math.sin(n)+a]}class Line{constructor(t,n,r){this.k=Math.tan(t),this.x0=n,this.y0=r}isGreater(t,n){var r=t-xBufferSize/2;return n-yBufferSize/2-this.y0>this.k*(r-this.x0)?0:1}}function inCircle(t,n,r,a,e,i){var f=((e-=xBufferSize/2)-r)**2+((i-=yBufferSize/2)-a)**2;return!!(f<n**2&f>t**2)}